import React, { Component } from 'react';


const ColorThief = require('colorthief');

// const img = "https://www.talkwalker.com/images/2020/blog-headers/image-analysis.png";

// ColorThief.getColor(img)
//     .then(color => { console.log(color) })
//     .catch(err => { console.log(err) })



class Header extends Component {

    constructor(props) {
        super(props);

        // Image element reference
        this.imgRef = React.createRef();
    }


    render() {
        return (
            <div className="header">
                <p className="header__title"><b>Song Analysis</b></p>
                <p className="header__subtitle">Learn about the song you are currently listening to.</p>
                <img
                    crossOrigin={"anonymous"}
                    ref={this.imgRef}
                    src={
                        "https://images.unsplash.com/photo-1516876437184-593fda40c7ce"
                    }
                    alt={"example"}
                    className={"example__img"}
                    onLoad={() => {
                        const colorThief = new ColorThief();
                        const img = this.imgRef.current;
                        const result = colorThief.getColor(img, 25);
                        console.log(result);
                    }}
                />
            </div>
        );
    }

}


export default Header;
